package com.iota.iri.utils.comparators;

import java.util.Arrays;

import org.junit.Assert;
import org.junit.Test;

public class TryteIndexComparatorTest {

    // currentIndex: [1,0]
    private static final String[] TRYTES = {
            "",
            "" };

    @Test
    public void shouldSortTrytesByCurrentIndex() {
        String tryteWithIndexZero = TRYTES[1];
        Arrays.sort(TRYTES, new TryteIndexComparator());
        Assert.assertEquals("should be at index 0", tryteWithIndexZero, TRYTES[0]);
    }
}
